mapper.update:
update trole
set NAME = #{name,jdbcType=VARCHAR},
  REMARK = #{remark,jdbcType=VARCHAR},
  SEQ = #{seq,jdbcType=INTEGER},
  PID = #{pid,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select count(*) from trole
where 1=1

  and  name like #{name,jdbcType=VARCHAR}

select
from trole where 1=1
and name like #{name,jdbcType=VARCHAR}

SELECT trole.ID, trole.NAME, trole.REMARK, trole.SEQ, trole.PID, tresource.ID resource_id, tresource.ICON resource_icon, tresource.NAME resource_name, tresource.REMARK resource_remark, tresource.SEQ resource_seq, tresource.URL resource_url, tresource.PID resource_pid, tresource.TRESOURCETYPE_ID resourcetype_id FROM trole JOIN trole_tresource ON trole.ID = trole_tresource.TROLE_ID JOIN tresource ON trole_tresource.TRESOURCE_ID = tresource.ID where 1=1
and trole.name like #{name,jdbcType=VARCHAR}
and trole.id =#{id,jdbcType=VARCHAR}
